import logo from './favicon.jpg';//logo.svg
import './App.css';
import React, { useState, useEffect } from 'react';
import Home from './pages/Home';
import Upload from './pages/Upload';
import ViewData from './pages/ViewData';
import Login from './pages/Login';

function App() {
  const [token, setToken] = useState(localStorage.getItem("token") || "");
  const [recargarColecciones, setRecargarColecciones] = useState(false);

  // Guarda el token en localStorage cada vez que cambia
  useEffect(() => {
    if (token) {
      localStorage.setItem("token", token);
    }
  }, [token]);

  // Llamado desde Upload para indicar que se debe recargar
  const notificarNuevaColeccion = () => {
    setRecargarColecciones(prev => !prev); // cambia el valor para que ViewData se entere
  };

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />

        {/*mostrar Login si no hay token*/}
        {!token ? (
          <Login setToken={setToken} />
        ) : (
          <>
            <p>üîê Sesi√≥n iniciada</p>
            <button onClick={() => {
                setToken("");
                localStorage.removeItem("token");
                window.location.reload();
              }}>
                Cerrar sesi√≥n
            </button>

          </>
        )}
        <br />
      </header>

      {/* Solo mostrar contenido si hay token */}
      {token && (
        <>
          <Home />
          <hr />
          <Upload onUploadSuccess={notificarNuevaColeccion} />
          <hr />
          <ViewData actualizar={recargarColecciones} />
        </>
      )}
    </div>
  );
}

export default App;
